import { Component, OnInit } from '@angular/core';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';
import { ServicesService } from 'src/app/services/services.service';
import { MatDialogRef, MAT_DIALOG_DATA } from '@angular/material/dialog';

@Component({
  selector: 'app-create',
  templateUrl: './create.component.html',
  styleUrls: ['./create.component.css']
})
export class CreateComponent implements OnInit {



  constructor(private fb: FormBuilder,
    private service: ServicesService,
    private dialogRef: MatDialogRef<CreateComponent>) { }

  validateForm!: FormGroup;

  ngOnInit(): void {
    this.validateForm = this.fb.group({

      name: ['', [Validators.required]],
      typeSpacecraft : ['', [Validators.required]],
      tonsOfThrust : [0, [Validators.required]],
      tonsOfWeight : [0, [Validators.required]],
      typeFuel : ['', [Validators.required]],
      height : [0, [Validators.required]],
      country : ['', [Validators.required]],
      objective : ['', [Validators.required]],
      activated : [Boolean, [Validators.required]],
      expirationDate : ['', [Validators.required]],
      peopleCapacity : [0, [Validators.required]],
      transportCapacity : [0, [Validators.required]],
      massiveSpeed : [0, [Validators.required]],
      propulsionSystem : ['', [Validators.required]],
      image: ['', [Validators.required]],


    });
  }


}
