import { Component, OnInit } from '@angular/core';
import { ResponseData, Spacecraft } from 'src/app/models/model';
import { ServicesService } from 'src/app/services/services.service';
import { MatDialog } from '@angular/material/dialog';
import { CreateComponent } from '../create/create.component';
import { DetailComponent } from '../detail/detail.component';


@Component({
  selector: 'app-home',
  templateUrl: './home.component.html',
  styleUrls: ['./home.component.css']
})
export class HomeComponent implements OnInit {

  constructor(private services: ServicesService, public dialog: MatDialog) {

  }


  countries: string[] = []
  fuels: string[] = []
  data: Spacecraft[] = []

  ngOnInit(): void {
    this.services.GetAll().subscribe(r => {
      this.data = r.body?.data

      this.services.GetCountries().subscribe(c => {
        this.countries = c.body?.data

      })

      this.services.GetFuels().subscribe(c => {
        this.fuels = c.body?.data
      })


    })
  }


  openCreate() {
    this.dialog.open(CreateComponent, {
      width: '40%'
    }).afterClosed().subscribe(result => {
      this.ngOnInit()
    });
  }

  details(item: Spacecraft) {
    this.dialog.open(DetailComponent, {
      width: '80%',
      height: '70%',
      data: item
    })
  }


}
<div class="settings">
  <div class="new">
    <button class="button" (click)="openCreate()" mat-button color="primary">Nuevo</button>
  </div>

  <div class="filters">
    <h4>Categoria</h4>
    <mat-form-field appearance="fill">
      <mat-select  >
        <mat-option value="Vehiculo Lanzadera">Vehiculo Lanzadera</mat-option>
        <mat-option value="Vehiculo no tripulado">Vehiculo no tripulado</mat-option>
        <mat-option value="Vehiculo tripulado">Vehiculo tripulado</mat-option>
      </mat-select>
    </mat-form-field>
  </div>


  <div class="filters">
    <h4>Pais - {{countries[0] | json}}</h4>
    <mat-form-field appearance="fill">
      <mat-select  >
        <mat-option *ngForm="let item of countries" value="{{item}}">{{item}}</mat-option>
        <mat-option *ngFor="let item of countries" value="{{item}}">{{item}}</mat-option>
      </mat-select>
    </mat-form-field>
  </div>

  <div class="filters">
    <h4>Combustible</h4>
    <mat-form-field appearance="fill">
      <mat-select  >
        <mat-option value="Vehiculo Lanzadera">Vehiculo Lanzadera</mat-option>
        <mat-option value="Vehiculo no tripulado">Vehiculo no tripulado</mat-option>
        <mat-option value="Vehiculo tripulado">Vehiculo tripulado</mat-option>
      </mat-select>
    </mat-form-field>
  </div>

  <div class="filters">
    <h4>Activos</h4>
    <mat-form-field appearance="fill">
      <mat-select  >
        <mat-option value="false">Si</mat-option>
        <mat-option value="true">No</mat-option>
      </mat-select>
    </mat-form-field>
  </div>
</div>

<div class="containerr" >
<mat-card *ngFor="let item of data" (click)="details(item)" class="example-card">
    <mat-card-header>

      <mat-card-title>{{item.name}}</mat-card-title>
      <mat-card-subtitle>{{item.type_spacecraft}}</mat-card-subtitle>
    </mat-card-header>
    <img mat-card-image src={{item.image}} alt={{item.name}}>
    <mat-card-content>
      <p>{{item.objective}} </p>
    </mat-card-content>
    <mat-card-actions>
      <button mat-button>Ver mas</button>
    </mat-card-actions>
  </mat-card>
</div>
